if (typeof happy === 'undefined') {
  throw Error(
    `You didn't even define the function happy... make it better! reread the lesson...`,
  )
}
if (typeof happy != 'function') {
  throw Error('happy must be a function')
}

if (happy.length != 1) {
  throw Error('You need 1 argument for this function!')
}

if (happy.toString().includes('{')) {
  throw Error('No curly braces {} ! ')
}
if (happy.toString().includes('}')) {
  throw Error('No curly braces {} ! ')
}

if (happy.toString().includes('return')) {
  throw Error('No keyword return allowed ! ')
}

if (happy.toString().includes('if')) {
  throw Error('No keyword if allowed ! ')
}
